<?phprequire_once('../dbconfigStamboom.php');// require_once('../Functions.php'); // personenSelecteren ($persoonkeuze,$persoonkeuze1,$persoonkeuze2);$db = new mysqli($dbConfig['dbhost'], $dbConfig['dbuser'], $dbConfig['dbpass'], $dbConfig['dbname']);if ($db->connect_errno > 0) {	echo 'Fout! : ' . $db->connect_error;}function getPersoonData ($db, $persoon_id) {	$sql = "SELECT *	FROM `personenregister`	WHERE `persoon_id` = $persoon_id";	$resultaat = $db->query($sql);	if (!$resultaat) {		echo "Fout: : " . $db->error . "<br/>";		return false;	}	$persoon = array(); //nu definieer ik hem wel eerst, zodat hij in ieder geval bestaat als ik het uitlees	while($row = $resultaat->fetch_assoc()){		$persoon = $row;		break; //while loop sowieso stoppen, want maar 1 persoon	}	return $persoon;}$persoon1 = getPersoonData($db, 1);$persoonkeuze = $persoon1['persoon_id'];$voornaam = $persoon1['voornaam'];$tweedenaam = $persoon1['tweedenaam'];$derdenaam = $persoon1['derdenaam'];$voorvoegsel_achternaam = $persoon1['voorvoegsel_achternaam'];$achternaam = $persoon1['achternaam'];$voorvoegsel_meisjesnaam = $persoon1['voorvoegsel_meisjesnaam'];$meisjesnaam = $persoon1['meisjesnaam'];$sql1 = "SELECT `persoon_id` , `voornaam` , `tweedenaam` , `derdenaam` , `voorvoegsel_achternaam` , `achternaam` , `voorvoegsel_meisjesnaam` , `meisjesnaam`FROM `personenregister`WHERE `persoon_id` = '2' ";$resultaat1 = $db->query($sql1);if (!$resultaat1) {	echo "Fout: ': " . $db->error . "<br/>";}while($row = $resultaat1->fetch_assoc()){	$persoonkeuze1 = $row['persoon_id'];	$voornaam1 = $row['voornaam'];	$tweedenaam1 = $row['tweedenaam'];	$derdenaam1 = $row['derdenaam'];	$voorvoegsel_achternaam1 = $row['voorvoegsel_achternaam'];	$achternaam1 = $row['achternaam'];	$voorvoegsel_meisjesnaam1 = $row['voorvoegsel_meisjesnaam'];	$meisjesnaam1 = $row['meisjesnaam'];}$sql2 = "SELECT `persoon_id` , `voornaam` , `tweedenaam` , `derdenaam` , `voorvoegsel_achternaam` , `achternaam` , `voorvoegsel_meisjesnaam` , `meisjesnaam`FROM `personenregister`WHERE `persoon_id` = '3' ";$resultaat2 = $db->query($sql2);if (!$resultaat2) {	echo "Fout: ': " . $db->error . "<br/>";}while($row = $resultaat2->fetch_assoc()){	$persoonkeuze2 = $row['persoon_id'];	$voornaam2 = $row['voornaam'];	$tweedenaam2 = $row['tweedenaam'];	$derdenaam2 = $row['derdenaam'];	$voorvoegsel_achternaam2 = $row['voorvoegsel_achternaam'];	$achternaam2 = $row['achternaam'];	$voorvoegsel_meisjesnaam2 = $row['voorvoegsel_meisjesnaam'];	$meisjesnaam2 = $row['meisjesnaam'];}?><html><head>	<title>Persoon wijzigen</title>	<link rel="icon"		  type="image/png"		  href="<?php echo $path; ?>/img/ZwitsalBasje.jpg" />	<meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1">	<link rel="stylesheet" href="<?php echo $path; ?>/css/style.css" type="text/css" /></head><body><div id="main_container" >	<div id="header_container" >		<img src="<?php echo $path; ?>/img/Veltens.png" alt="logo" title="Veltens"  width="96px" align="center"/>		<img src="<?php echo $path; ?>/img/BAFWARE.png" alt="logo" title="BAFWARE"  width="500px" align="center"/>		<img src="<?php echo $path; ?>/img/ZwitsalBasje.jpg" alt="logo" title="ZwitsalBasje.jpg"  width="96px" align="center"/>	</div>	<div id="input_container" >		<div width="100%" >			<table width="100%" border="1" cellpadding="3" cellspacing="1">				<tr>					<td width="10%" bgcolor="#C5CED3"><a href="<?php echo $path; ?>/stamboom.php">home</a></td>					<td width="10%" bgcolor="#D6DDE0">persoon toevoegen/wijzigen</td>					<td width="10%" bgcolor="#E6EAEC"></td>					<td width="10%" bgcolor="#EDEEF1"></td>					<td width="10%" bgcolor="#F4F5F7">Veltens stamboom</td>					<td width="10%" bgcolor="#F4F5F7">nummereringslogica</td>					<td width="10%" bgcolor="#EDEEF1"></td>					<td width="30%" bgcolor="#EDEEF1"></td>				</tr>			</table>			<table width="100%" border="0" cellspacing="1" cellpadding=